  | SummaryMessage      // {"type":"summary", "summary":"..."}
  | UserMessage         // {"type":"user", "message":{"role":"user", "content":[...]}}
  | AssistantMessage    // {"type":"assistant", "message":{"role":"assistant", "content":[...]}}
