  it('should identify text content', () => {
    const input = { type: 'text', text: 'Hello world' };
    expect(identifyContentType(input)).toBe(ContentType.Text);
  });

  it('should identify thinking content', () => {
    const input = { 
      type: 'thinking', 
      thinking: 'Let me analyze this...',
      signature: 'EqAIC...'
    };
    expect(identifyContentType(input)).toBe(ContentType.Thinking);
  });

  it('should identify tool use content', () => {
    const input = { 
      type: 'tool_use',
      id: 'toolu_123',
      name: 'Bash',
      input: { command: 'ls' }
    };
    expect(identifyContentType(input)).toBe(ContentType.ToolUse);
  });

  it('should identify tool result content', () => {
    const input = { 
      type: 'tool_result',
      tool_use_id: 'toolu_123',
      content: 'file1.txt\nfile2.txt',
      is_error: false
    };
    expect(identifyContentType(input)).toBe(ContentType.ToolResult);
  });

  it('should identify image content', () => {
    const input = { 
      type: 'image',
      source: {
        type: 'base64',
        media_type: 'image/png',
        data: 'base64data...'
      }
    };
    expect(identifyContentType(input)).toBe(ContentType.Image);
  });

  it('should return unknown for unrecognized content', () => {
    const input = { type: 'unknown_type' };
    expect(identifyContentType(input)).toBe(ContentType.Unknown);
  });

  it('should handle null input', () => {
    expect(identifyContentType(null)).toBe(ContentType.Unknown);
  });

