/**
 * Identifies the type of a content block
 * @param content - Content block object
 * @returns ContentType enum value
 */
export function identifyContentType(content: any): ContentType {
  // Handle null/undefined cases
  if (!content || typeof content !== 'object') {
    return ContentType.Unknown;
  }

  // Check the type field
  switch (content.type) {
    case 'text':
      return ContentType.Text;

    case 'thinking':
      return ContentType.Thinking;

    case 'tool_use':
      return ContentType.ToolUse;

    case 'tool_result':
      return ContentType.ToolResult;

    case 'image':
      return ContentType.Image;

    default:
      return ContentType.Unknown;
  }
}

