 */
export interface ParseResult {
  success: boolean;
  data?: BaseLogEntry;
  messageType?: MessageType;
  error?: {
    type: 'ParseError' | 'EmptyInput' | 'ValidationError';
    message: string;
  };
}

/**
 * Parses a JSONL line into a typed log entry
 * @param line - Raw JSONL string
 * @returns ParseResult with parsed data or error
 */
export function parseJsonLine(line: string): ParseResult {
  // Handle empty or whitespace-only input
  if (!line || !line.trim()) {
    return {
      success: false,
      error: {
        type: 'EmptyInput',
        message: 'Input line is empty or contains only whitespace'
      }
    };
  }

  try {
    // Parse the JSON
    const parsed = JSON.parse(line);

    // Basic validation - ensure it's an object
    if (typeof parsed !== 'object' || parsed === null) {
      return {
        success: false,
        error: {
          type: 'ValidationError',
          message: 'Parsed JSON is not an object'
        }
      };
    }

    // Identify the message type
    const messageType = identifyMessageType(parsed);

    return {
      success: true,
      data: parsed as BaseLogEntry,
      messageType
    };
  } catch (error) {
    // Handle JSON parsing errors
    return {
      success: false,
      error: {
        type: 'ParseError',
        message: error instanceof Error ? error.message : 'Invalid JSON'
      }
    };
  }
}

