describe('formatRelativeTime', () => {
  beforeEach(() => {
    // Mock current time to 2025-06-25T20:00:00.000Z
    vi.useFakeTimers();
    vi.setSystemTime(new Date('2025-06-25T20:00:00.000Z'));
  });

  afterEach(() => {
    vi.useRealTimers();
  });

  it('should format time less than a minute ago', () => {
    const timestamp = '2025-06-25T19:59:30.000Z';
    expect(formatRelativeTime(timestamp)).toBe('just now');
  });

  it('should format time in minutes', () => {
    const timestamp = '2025-06-25T19:55:00.000Z';
    expect(formatRelativeTime(timestamp)).toBe('5 minutes ago');
  });

  it('should format time in hours', () => {
    const timestamp = '2025-06-25T18:00:00.000Z';
    expect(formatRelativeTime(timestamp)).toBe('2 hours ago');
  });

  it('should format time in days', () => {
    const timestamp = '2025-06-23T20:00:00.000Z';
    expect(formatRelativeTime(timestamp)).toBe('2 days ago');
  });

  it('should format time over a week as date', () => {
    const timestamp = '2025-06-10T20:00:00.000Z';
    expect(formatRelativeTime(timestamp)).toBe('Jun 10, 2025');
  });

  it('should handle invalid timestamps', () => {
    expect(formatRelativeTime('invalid')).toBe('Invalid date');
  });
});

