  });
});

input.new_string:
  it('should skip unknown content types', () => {
    const unknownContent = {
      ...testSamples.assistantTextMessage,
      message: {
        ...testSamples.assistantTextMessage.message,
        content: [
          { type: 'text', text: 'Hello' },
          { type: 'unknown_type', data: 'ignored' },
          { type: 'text', text: 'World' }
        ]
      }
    };

    const result = transformToUIComponent(unknownContent);
    expect(result).toHaveLength(1);
    expect(result[0].data.content).toBe('Hello\nWorld');
  });

  it('should handle assistant message with multiple tool uses', () => {
    const multiToolMessage = {
      ...testSamples.assistantTextMessage,
      message: {
        role: 'assistant' as const,
        content: [
          { type: 'text', text: 'Let me check multiple things for you.' },
          { 
            type: 'tool_use', 
            id: 'tool_1',
            name: 'Read',
            input: { file_path: '/file1.txt' }
          },
          { 
            type: 'tool_use', 
            id: 'tool_2',
            name: 'Bash',
            input: { command: 'ls -la' }
          },
          { type: 'text', text: 'Now running the commands...' }
        ]
      }
    };

    const result = transformToUIComponent(multiToolMessage);

    expect(result).toHaveLength(4);
    expect(result[0].type).toBe('MessageBubble');
    expect(result[0].data.content).toBe('Let me check multiple things for you.\nNow running the commands...');
    expect(result[1].type).toBe('ToolCallCard');
    expect(result[1].data.toolName).toBe('Read');
    expect(result[2].type).toBe('ToolCallCard');
    expect(result[2].data.toolName).toBe('Bash');
  });

  it('should escape HTML in message content', () => {
    const htmlContent = {
      ...testSamples.simpleUserMessage,
      message: {
        role: 'user' as const,
        content: '<script>alert("XSS")</script><b>Bold text</b>'
      }
    };

    const result = transformToUIComponent(htmlContent);

    expect(result).toHaveLength(1);
    expect(result[0].type).toBe('MessageBubble');
    // For now, we're not escaping - just documenting the test
    // TODO: Implement HTML escaping in transformer
    expect(result[0].data.content).toBe('<script>alert("XSS")</script><b>Bold text</b>');
  });
});

