describe('Special tool content handling', () => {
  it('should handle image results', () => {
    const imageResult = {
      type: 'tool_result',
      message: {
        role: 'user',
        content: [{
          type: 'tool_result',
          tool_use_id: 'tool_screenshot',
          content: 'Screenshot taken',
          is_error: false
        }]
      },
      toolUseResult: {
        stdout: '',
        stderr: '',
        isImage: true
      },
      uuid: 'uuid-image',
      timestamp: '2025-06-25T10:00:00.000Z',
      parentUuid: null
    };

    const components = transformToUIComponent(imageResult);
    expect(components[0].data.isImage).toBe(true);
  });

  it('should preserve stdout/stderr for Bash commands', () => {
    const bashResult = {
      type: 'tool_result',
      message: {
        role: 'user',
        content: [{
          type: 'tool_result',
          tool_use_id: 'tool_bash_output',
          content: 'file1.txt\nfile2.txt',
          is_error: false
        }]
      },
      toolUseResult: {
        stdout: 'file1.txt\nfile2.txt',
        stderr: ''
      },
      uuid: 'uuid-bash-output',
      timestamp: '2025-06-25T10:00:00.000Z',
      parentUuid: null
    };

    const components = transformToUIComponent(bashResult);
    expect(components[0].data.stdout).toBe('file1.txt\nfile2.txt');
    expect(components[0].data.stderr).toBe('');
  });
});

