it(`should parse ${name} tool result`, () => {
  const resultMessage = {
    type: 'message',
    message: {
      role: 'user',
      content: [
        {
          type: 'tool_result',
          tool_use_id: `tool_${name}_123`,
          content: `${name} output`,
          is_error: false
        }
      ]
    },
    uuid: `uuid-result-${name}`,
    timestamp: '2025-06-25T10:00:05.000Z',
    parentUuid: `uuid-${name}`
  };

  const components = transformToUIComponent(resultMessage);
  expect(components).toHaveLength(1);
  expect(components[0].type).toBe('ToolResultCard');
  expect(components[0].data.toolUseId).toBe(`tool_${name}_123`);
  expect(components[0].data.content).toContain(`${name} output`);
});

