import { Observable, from } from 'rxjs';
import { map } from 'rxjs/operators';
import { parseJsonLine } from '../core/parser';
import { transformToUIComponent } from '../transformers/ui-transformer';
import { enhanceWithMetadata } from '../transformers/metadata-enhancer';
import { UIComponent } from '../core/types';

/**
 * Creates an observable stream of UI components from a log source
 * @param source - Array of JSONL strings (for now)
 * @returns Observable stream of UI components
 */
export function createLogStream(source: string[]): Observable<UIComponent> {
  return from(source).pipe(
    // Parse each JSONL line
    map(line => parseJsonLine(line)),

    // Transform to UI components (returns array)
    map(parsed => transformToUIComponent(parsed)),

    // Flatten the array of components
    map(components => components.map(comp => enhanceWithMetadata(comp))),

    // Emit each component individually
    map(components => components[0]) // For now, just emit first
  );
}

