export function createLogStream(source: string[]): Observable<UIComponent> {
  return from(source).pipe(
    // Parse each JSONL line
    map(line => {
      const parsed = parseJsonLine(line);
      if (!parsed) throw new Error(`Failed to parse line: ${line}`);
      return parsed;
    }),

    // Transform to UI components (returns array)
    map(parsed => {
      const components = transformToUIComponent(parsed);
      if (!components || components.length === 0) {
        console.warn('No components from:', parsed);
      }
      return components;
    }),

    // Flatten the array of components and enhance each
    mergeMap(components => 
      from(components).pipe(
        map(comp => enhanceWithMetadata(comp))
      )
    )
  );
}

