    it('should handle malformed JSON gracefully', (done) => {
      const lines = [
        '{"type": "user", "message": {"role": "user", "content": "Valid"}, "uuid": "1", "timestamp": "2025-06-25T10:00:00Z"}',
        'malformed json {',
        '{"type": "assistant", "message": {"role": "assistant", "content": "Also valid"}, "uuid": "2", "timestamp": "2025-06-25T10:00:01Z"}'
      ];

      const stream$ = createLogStream(lines);
      const results: any[] = [];
      const errors: any[] = [];

      stream$.subscribe({
        next: (component) => results.push(component),
        error: (err) => {
          errors.push(err);
          done();
        },
        complete: () => {
          // Should have caught the error and stopped
          done(new Error('Stream should have errored'));
        }
      });
    });

