      const results: any[] = [];

      stream$.subscribe({
        next: (component) => results.push(component),
        complete: () => {
          expect(results).toHaveLength(2);
          expect(results[0].type).toBe('MessageBubble');
          expect(results[0].data.content).toBe('Let me help');
          expect(results[1].type).toBe('ToolCallCard');
          expect(results[1].data.toolName).toBe('Bash');
          done();
        },
        error: done
      });
