/**
 * Creates stream from array of JSONL strings
 */
function createLogStreamFromArray(lines: string[]): Observable<UIComponent> {
  return from(source).pipe(
    // Filter out empty lines
    map(line => line.trim()),
    mergeMap(line => {
      if (!line) {
        // Skip empty lines by returning empty observable
        return from([]);
      }

      // Parse JSONL line
      const result = parseJsonLine(line);
      if (!result.success) {
        throw new Error(`Failed to parse line: ${result.error}`);
      }

      // Transform to UI components and enhance
      const components = transformToUIComponent(result.data);
      return from(components).pipe(
        map(comp => enhanceWithMetadata(comp))
      );
    })
  );
}

