/**
 * Creates stream from array of JSONL strings
 */
function createLogStreamFromArray(lines: string[]): Observable<UIComponent> {
  return from(lines).pipe(
    // Filter out empty lines
    map(line => line.trim()),
    mergeMap(line => {
      if (!line) {
        // Skip empty lines by returning empty observable
        return from([]);
      }

      // Parse JSONL line
      const result = parseJsonLine(line);
      if (!result.success) {
        throw new Error(`Failed to parse line: ${result.error}`);
      }

      // Transform to UI components and enhance
      const components = transformToUIComponent(result.data);
      return from(components).pipe(
        map(comp => enhanceWithMetadata(comp))
      );
    })
  );
}

/**
 * Creates stream from file path
 */
function createLogStreamFromFile(
  filePath: string,
  options: LogStreamOptions
): Observable<UIComponent> {
  return new Observable(subscriber => {
    // For simple case, just read the whole file
    if (!options.tail && !options.fileReader) {
      readFile(filePath, 'utf8')
        .then(content => {
          const lines = content.split('\n');
          createLogStreamFromArray(lines).subscribe(subscriber);
        })
        .catch(err => subscriber.error(err));
    } else {
      // TODO: Implement streaming file read
      subscriber.error(new Error('Streaming not implemented yet'));
    }
  });
}

