it('should tail from beginning when fromBeginning is true', async () => {
  // Create file with existing content
  const lines = [
    '{"type": "user", "message": {"role": "user", "content": "Line 1"}, "uuid": "1", "timestamp": "2025-06-25T10:00:00Z"}',
    '{"type": "user", "message": {"role": "user", "content": "Line 2"}, "uuid": "2", "timestamp": "2025-06-25T10:00:01Z"}'
  ];
  await writeFile(testFile, lines.join('\n') + '\n');

  // Start tailing from beginning
  const stream$ = createLogStream(testFile, { 
    tail: true,
    fromBeginning: true,
    pollInterval: 100
  });

  // Collect first 3 components
  const resultsPromise = firstValueFrom(
    stream$.pipe(
      take(3),
      toArray(),
      timeout({ first: 5000, with: () => [] })
    )
  );

  // Append new line after delay
  setTimeout(async () => {
    const newLine = '{"type": "user", "message": {"role": "user", "content": "Line 3"}, "uuid": "3", "timestamp": "2025-06-25T10:00:02Z"}';
    await appendFile(testFile, newLine + '\n');
  }, 200);

  const results = await resultsPromise;

  // Should get all lines including existing ones
  expect(results).toHaveLength(3);
  expect(results[0].data.content).toBe('Line 1');
  expect(results[1].data.content).toBe('Line 2');
  expect(results[2].data.content).toBe('Line 3');
});

