it('should handle file rotation', async () => {
  // Create initial file
  const initialLine = '{"type": "user", "message": {"role": "user", "content": "Before rotation"}, "uuid": "1", "timestamp": "2025-06-25T10:00:00Z"}';
  await writeFile(testFile, initialLine + '\n');

  // Start tailing
  const stream$ = createLogStream(testFile, { 
    tail: true,
    pollInterval: 100
  });

  const results: any[] = [];
  const subscription = stream$.subscribe({
    next: (component) => {
      results.push(component);
    }
  });

  // Wait for polling to start
  await new Promise(resolve => setTimeout(resolve, 150));

  // Simulate file rotation
  await writeFile(testFile, '');

  // Write new content
  const newLine1 = '{"type": "user", "message": {"role": "user", "content": "After rotation 1"}, "uuid": "2", "timestamp": "2025-06-25T10:00:01Z"}';
  await writeFile(testFile, newLine1 + '\n');

  // Wait for polling
  await new Promise(resolve => setTimeout(resolve, 200));

  const newLine2 = '{"type": "user", "message": {"role": "user", "content": "After rotation 2"}, "uuid": "3", "timestamp": "2025-06-25T10:00:02Z"}';
  await appendFile(testFile, newLine2 + '\n');

  // Wait for polling
  await new Promise(resolve => setTimeout(resolve, 200));

  subscription.unsubscribe();

  // Should get lines after rotation
  expect(results.length).toBeGreaterThanOrEqual(2);
  const contents = results.map(r => r.data.content);
  expect(contents).toContain('After rotation 1');
  expect(contents).toContain('After rotation 2');
});

