it('should handle file rotation', async () => {
  // Create initial file
  const initialLine = '{"type": "user", "message": {"role": "user", "content": "Before rotation"}, "uuid": "1", "timestamp": "2025-06-25T10:00:00Z"}';
  await writeFile(testFile, initialLine + '\n');

  // Start tailing
  const stream$ = createLogStream(testFile, { 
    tail: true,
    pollInterval: 100
  });

  // Collect next 2 components
  const resultsPromise = firstValueFrom(
    stream$.pipe(
      take(2),
      toArray(),
      timeout({ first: 5000, with: () => [] })
    )
  );

  // Simulate file rotation
  setTimeout(async () => {
    // Truncate file (rotation)
    await writeFile(testFile, '');

    // Write new content
    const newLine1 = '{"type": "user", "message": {"role": "user", "content": "After rotation 1"}, "uuid": "2", "timestamp": "2025-06-25T10:00:01Z"}';
    await appendFile(testFile, newLine1 + '\n');
  }, 200);

  setTimeout(async () => {
    const newLine2 = '{"type": "user", "message": {"role": "user", "content": "After rotation 2"}, "uuid": "3", "timestamp": "2025-06-25T10:00:02Z"}';
    await appendFile(testFile, newLine2 + '\n');
  }, 400);

  const results = await resultsPromise;

  // Should get lines after rotation
  expect(results).toHaveLength(2);
  expect(results[0].data.content).toBe('After rotation 1');
  expect(results[1].data.content).toBe('After rotation 2');
});

