/**
 * Calculates the number of lines in content
 */
export function calculateLineCount(content: string): number {
  if (!content) return 0;
  const lines = content.split('\n');
  // Don't count trailing empty line
  return content.endsWith('\n') ? lines.length - 1 : lines.length;
}

/**
 * Determines if content should be collapsed based on line count
 */
export function shouldCollapseContent(content: string, threshold: number = 10): boolean {
  const lineCount = calculateLineCount(content);
  const contentLength = content.length;

  // Collapse if over line threshold or very long
  return lineCount > threshold || contentLength > 500;
}

