input.old_string:
/**
 * Creates a Socket.io server for streaming log updates
 * @param logStream - Observable stream of UI components
 * @param options - Server options
 * @returns Socket.io server instance
 */
export function createRealtimeServer(
  logStream: Observable<UIComponent>,
  options: RealtimeServerOptions = {}
): SocketServer {
  const {
    port = 3001,
    cors = ['http://localhost:3000'],
    maxBufferSize = 100
  } = options;

  // Create HTTP server
  const httpServer = createServer();

  // Create Socket.io server
  const io = new SocketServer(httpServer, {
    cors: {
      origin: cors,
      methods: ['GET', 'POST']
    }
  });

  // Track client connections
  const clients = new Map<string, ClientConnection>();

  // Handle new connections
  io.on('connection', (socket) => {
    console.log(`Client connected: ${socket.id}`);

    // Initialize client connection
    const client: ClientConnection = {
      id: socket.id,
      sessionId: '',
      buffer: []
    };
    clients.set(socket.id, client);

    // Handle session subscription
    socket.on('subscribe', (sessionId: string) => {
      console.log(`Client ${socket.id} subscribing to session: ${sessionId}`);

      // Update client session
      client.sessionId = sessionId;

      // Join session room
      socket.join(`session:${sessionId}`);

      // Send buffered components if any
      if (client.buffer.length > 0) {
        socket.emit('components:batch', client.buffer);
        client.buffer = [];
      }

      // Subscribe to log stream
      if (!client.subscription) {
        client.subscription = logStream.subscribe({
          next: (component) => {
            // Emit to all clients in the session room
            io.to(`session:${sessionId}`).emit('component', component);
          },
          error: (error) => {
            console.error('Stream error:', error);
            socket.emit('error', { message: error.message });
          },
          complete: () => {
            console.log('Stream completed');
            socket.emit('stream:complete');
          }
        });
      }
    });

