// Connect to WebSocket server for live updates
const connectLive = () => {
  if (liveSocket) {
    liveSocket.close()
  }

  setIsLiveMode(true)
  setError(null)
  setComponents([])

  // Import socket.io dynamically
  import('socket.io-client').then(({ io }) => {
    const socket = io('http://localhost:3001')

    socket.on('connect', () => {
      console.log('Connected to live server')
      setError(null)
    })

    socket.on('component', (component: UIComponent) => {
      setComponents(prev => [...prev, component])
    })

    socket.on('error', (err: any) => {
      setError(`Live connection error: ${err.message || err}`)
    })

    socket.on('disconnect', () => {
      if (isLiveMode) {
        setError('Disconnected from live server')
      }
    })

    setLiveSocket(socket)
  }).catch(err => {
    setError('Failed to load socket.io-client')
  })
}

