.conversation-info {
  font-size: 0.875rem;
  font-weight: normal;
  margin-left: 0.5rem;
}

/* Project Browser Styles */
.project-browser {
  padding: 2rem;
  max-width: 1200px;
  margin: 0 auto;
}

.browser-header {
  text-align: center;
  margin-bottom: 2rem;
}

.browser-header h2 {
  font-size: 2rem;
  margin-bottom: 0.5rem;
  color: #667eea;
}

.browser-content {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 2rem;
  height: 600px;
}

.projects-panel,
.conversations-panel {
  background: white;
  border: 1px solid #e5e7eb;
  border-radius: 8px;
  padding: 1rem;
  overflow: hidden;
  display: flex;
  flex-direction: column;
}

.projects-panel h3,
.conversations-panel h3 {
  margin: 0 0 1rem 0;
  color: #4b5563;
  font-size: 1.125rem;
}

.project-list,
.conversation-list {
  flex: 1;
  overflow-y: auto;
}

.project-item,
.conversation-item {
  padding: 0.75rem;
  border: 1px solid #e5e7eb;
  border-radius: 4px;
  margin-bottom: 0.5rem;
  cursor: pointer;
  transition: all 0.2s;
}

.project-item:hover,
.conversation-item:hover {
  background: #f9fafb;
  border-color: #667eea;
}

.project-item.selected {
  background: #e0e7ff;
  border-color: #667eea;
}

.project-name,
.conversation-name {
  font-weight: 500;
  color: #1f2937;
  margin-bottom: 0.25rem;
}

.project-meta,
.conversation-meta {
  font-size: 0.75rem;
  color: #6b7280;
}

.conversation-meta .separator {
  margin: 0 0.5rem;
}

.loading {
  text-align: center;
  padding: 2rem;
  color: #667eea;
}

.browse-button {
  background: #8b5cf6 !important;
}

.browse-button:hover:not(:disabled) {
  background: #7c3aed !important;
}

