    liveSocket.emit('select-conversation', { projectPath, conversationFile })
  }
}

input.new_string:
// Handle conversation selection from browser
const handleSelectConversation = (projectPath: string, conversationFile: string) => {
  if (!liveSocket || !liveSocket.connected) {
    // Store the selection for after connection
    setComponents([])
    setIsLiveMode(true)
    setError(null)

    // Import socket.io dynamically
    import('socket.io-client').then(({ io }) => {
      const socket = io('http://localhost:3001')

      socket.on('connect', () => {
        console.log('Connected to live server')
        setError(null)
        // Immediately emit the selection after connection
        socket.emit('select-conversation', { projectPath, conversationFile })
      })

      socket.on('component', (component: UIComponent) => {
        setComponents(prev => [...prev, component])
      })

      socket.on('error', (err: any) => {
        setError(`Live connection error: ${err.message || err}`)
      })

      socket.on('disconnect', () => {
        if (isLiveMode) {
          setError('Disconnected from live server')
        }
      })

      socket.on('stream:started', ({ projectPath, conversationFile }) => {
        setCurrentConversation({ project: projectPath, file: conversationFile })
        setShowBrowser(false)
      })

      setLiveSocket(socket)
    }).catch(err => {
      setError('Failed to load socket.io-client')
    })
  } else {
    setComponents([])
    liveSocket.emit('select-conversation', { projectPath, conversationFile })
  }
}

