      "edit_mode": {
        "type": "string",
        "enum": ["replace", "insert", "delete"],
        "description": "The type of edit to make (replace, insert, delete). Defaults to replace"
      }
    },
    "required": ["notebook_path", "new_source"],
    "additionalProperties": false
  }
}
